package ru.post.PostApp.api.dto.request;public enum PostItemType {    LETTER("LTR", "Письмо"),    PARCEL("PRC", "Посылка"),    REGISTERED_LETTER("RLT", "Заказное письмо");    private final String code;    private final String description;    PostItemType(String code, String description) {        this.code = code;        this.description = description;    }    public String getCode() { return code; }    public String getDescription() { return description; }    public static PostItemType getTypeByCode(String code) {        for (PostItemType type : values()) {            if (type.code.equals(code)) {                return type;            }        }        throw new IllegalArgumentException("Unknown type code: " + code);    }}